rule "cloudflare-threat"
when
  has_field("EdgePathingOp")
then
// Build  threat code for use in lookup later.
// According to https://developers.cloudflare.com/logs/tutorials/parsing-json-log-data/
let threat_code = concat(concat( concat(to_string($message.EdgePathingSrc), "."),
                                 concat(to_string($message.EdgePathingOp), ".")),
                                to_string($message.EdgePathingStatus));
// Looks like "user.ban.ipr16"
set_field("ThreatCode", threat_code);

// Look up the text description from a pipeline rule.
let threat_type = lookup_value("cloudflare-threat-lookup-table", threat_code);
debug(threat_type);
set_field("ThreatType", threat_type);
debug(threat_type != "No Threat");
set_field("Threat", to_string(threat_type) != "No Threat");
end

rule "ClientIP geoip lookup"
when
  has_field("ClientIP")
then
  let geo = lookup("geo-lookup-table", $message.ClientIP);
  debug($message.ClientIP);
  set_field("ClientIpGeolocation", geo["coordinates"]);
  set_field("ClientIpGeoCountryCode", geo["country"].iso_code);
  set_field("ClientIpGeoCountry", geo["country"].names.en);
  set_field("ClientIpGeoCityCode", geo["city"].names.en);
end
